import React from 'react';
import { Text, StyleSheet, View, SafeAreaView, FlatList, TouchableOpacity, Pressable, ScrollView } from 'react-native';

// local json for testing
import data from '../Data/Experiment.json'

const Chapters = ({navigation, route}) => {

    var mangas = Object.keys(data[route.params])

    return (
        <View style={styles.container}>
            <Text style = {styles.title}>manga_reader</Text>
            <Text style = {styles.manga_title}> {route.params}</Text>
            <View style={styles.itemslist}>
                {mangas.map(manga =>{
                    <Pressable onPress = {() => navigation.navigate('Display',data[route.params][item])}>
                        <Text style={styles.item}>{manga}</Text>
                    </Pressable>
                })}
            </View>
        </View>
    );
}

// alignText not working on android
const styles = StyleSheet.create({
    container: {
        height:'100%',
        width:'100%',
        backgroundColor: 'black',
    },
    title: {
        alignSelf: 'center',
        color: 'white',
        fontSize: 35,
        marginTop: 50,
    },
    manga_title:{
        alignSelf: 'flex-start',
        marginLeft: 30,
        color: 'red',
        fontSize: 35,
        marginTop: 50,
    },
    itemslist: {
        flexDirection: 'row-reverse',
        justifyContent: 'space-between',
        flexWrap:"wrap-reverse",
    },
    item: {
        height:40,
        width:70,
      marginTop: 20,
      fontSize: 20,
      backgroundColor: 'lightblue',
      padding: 10,
    }
});

export default Chapters;
