import React, {useState, useEffect} from 'react';
import { ActivityIndicator, StyleSheet, ScrollView, Image, Text } from 'react-native';

const Display = ({route}) => {

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState();

    const fetchData = async (url) => {
      const response = await fetch(url);
      return response.json();
    };

    const getMangas = () => {
      try {
        // fetch(`http://localhost:5000/${route.params.title}/${route.params.chapter}`).then((data) => {
        fetchData(`http://localhost:5000/One Piece/990`).then((data) => {
          setData(data);
          setLoading(false);
        });
      } catch (error) {
        console.error(error);
      }
    };


    useEffect(() => {
      getMangas();
    }, []);
    console.log(data)
    return (
        <ScrollView style={styles.container}>
            {isLoading ? <ActivityIndicator/> : (
                data.map(page=>(
                    <Image style={styles.page} source={{uri: page}}></Image>
                ))
            )}
        </ScrollView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex:1,
        backgroundColor: 'black',
    },
    page:{
        flex:1,
        height:500,
        resizeMode: 'contain',
    }
});

export default Display;
