import React,{ useEffect, useState } from 'react';
import { ActivityIndicator, Text, StyleSheet, Pressable, View } from 'react-native';

const Home = ({ navigation }) => {

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState();

    const fetchData = async (url) => {
      const response = await fetch(url);
      return response.json();
    };

    const getMangas = () => {
      try {
        fetchData("http://localhost:5000/").then((data) => {
          setData(data);
          setLoading(false);
        });
      } catch (error) {
        console.error(error);
      }
    };


    useEffect(() => {
      getMangas();
    }, []);

    return (

        <View style = {styles.container}>
            <Text style = {styles.title}>manga_reader</Text>
            <View style={styles.itemslist}>
                {isLoading ? <ActivityIndicator/> : (
                    Object.keys(data).map( (manga,index) => (
                        <Pressable style = {styles.box} onPress={() => navigation.navigate('Chapters',manga,Object.values(data)[index])}>
                            <Text style = {{fontSize:20, alignSelf:'flex-start'}} >{manga}</Text>
                            <Text style = {{fontSize:15,alignSelf:'flex-end'}} >latest chapter : {Object.values(data)[index]}</Text>
                        </Pressable>
                    ))
                )}
            </View>
        </View> 
    );
}

const styles = StyleSheet.create({
    container: {
        flex : 1,
        backgroundColor: 'black',
        alignContent: 'center',
    },
    title: {
        textAlign: 'center',
        color: 'white',
        fontSize: 35,
        marginTop: 50,
    },
    itemslist: {
        marginTop:50,
    },
    box:{
        height:100,
        width:200,
        alignSelf: 'center',
        justifyContent: 'center',
        backgroundColor: 'lightblue',
        padding: 15,
        marginTop: 20,
    }
});

export default Home;
