import React,{ useEffect, useState } from 'react';
import { ActivityIndicator, Text, StyleSheet, Pressable, View, FlatList } from 'react-native';

const Home = ({ navigation }) => {

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState();

    const fetchData = async (url) => {
      const response = await fetch(url);
      return response.json();
    };

    const getMangas = () => {
      try {
        fetchData("http://localhost:5000/").then((data) => {
          setData(data);
          setLoading(false);
        });
      } catch (error) {
        console.error(error);
      }
    };


    useEffect(() => {
      getMangas();
    }, []);

    return (
        // <View style = {styles.container}>
        //     <Text style = {styles.text}>manga_reader</Text>
        //         <View style = {styles.list}>
        //             <FlatList
        //                 data={Object.keys(data)}
        //                 keyExtractor = {item => item}
        //                 renderItem={({ item }) => (
        //                     <Pressable onPress={() => navigation.navigate('Chapters',item)}>
        //                         <Text style={styles.item}>{item}</Text>
        //                     </Pressable>
        //                 )}
        //             />
            <View style={{ flex: 1, padding: 24 }}>
                {isLoading ? <ActivityIndicator/> : (
                    <FlatList
                        data={[Object.keys(data), Object.values(data)]}
                        keyExtractor = {item => item}
                        renderItem={({ item }) => (
                            <Pressable onPress={() => navigation.navigate('Chapters',item)}>
                                <Text style={styles.item}>{item}</Text>
                            </Pressable>
                        )}
                    />
                )}
            </View>
        //         </View>
        // </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex : 1,
        backgroundColor: 'black',
        alignContent: 'center',
    },
    text: {
        textAlign: 'center',
        color: 'white',
        fontSize: 35,
        marginTop: 50,
    },
    list: {
      marginTop: 80,
      alignItems: 'center'
    },
    item: {
        alignSelf: 'center',
        backgroundColor: 'lightblue',
        padding: 15,
        marginTop: 20,
        fontSize: 20,
    }
});

export default Home;
