import React,{ useEffect, useState } from 'react';
import { ActivityIndicator, Text, StyleSheet, Pressable, View, ScrollView } from 'react-native';

const Home = ({ navigation }) => {

  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState();

  const fetchData = async (url) => {
    const response = await fetch(url);
    return response.json();
  };

  const getMangas = () => {
    try {
      fetchData("http://localhost:5000/").then((data) => {
        setData(data);
        setLoading(false);
      });
    } catch (error) {
      console.error(error);
    }
  };


  useEffect(() => {
    getMangas();
  }, []);

  const data2 = {"One Piece" : 110000,"Naruto" : 120938,"One punch man" : 120938,"Hunter x Hunter" : 12398123,"Berserk" : 13123,}

  return (

    <ScrollView style = {styles.container}>
      <Text style = {styles.title}>manga_reader</Text>
      <View style={styles.itemslist}>
        {isLoading ? <ActivityIndicator/> : (
          Object.keys(data2).map( (manga,index) => (
            <Pressable style = {styles.box} onPress={() => navigation.navigate('Chapters',{ 'title' : manga, 'chapters' : Object.values(data)[index]})}>
              <Text style = {{fontSize:20, alignSelf:'flex-start'}} >{manga}</Text>
              <Text style = {{fontSize:15,alignSelf:'flex-end'}} >latest chapter : {Object.values(data2)[index]}</Text>
            </Pressable>
          ))
        )}
      </View>
    </ScrollView> 
  );
}

const styles = StyleSheet.create({
  container: {
    flex : 1,
    backgroundColor: 'black',
    alignContent: 'center',
  },
  title: {
    textAlign: 'center',
    color: 'white',
    fontSize: 35,
    marginTop: 50,
  },
  itemslist: {
    marginTop:50,
  },
  box:{
    height:100,
    width:200,
    alignSelf: 'flex-start',
    justifyContent: 'center',
    backgroundColor: 'lightblue',
    padding: 15,
    marginTop: 20,
    marginHorizontal:30,
    clipPath: `polygon(0, 15%)`
    // clipPath: `polygon(0 0, 83% 0, 100% 20%, 100% 100%, 19% 100%, 0 79%)`
  }
});

export default Home;
