import React, { useState } from 'react';
import { v4 as uuid_v4 } from 'uuid';
import { Text, StyleSheet, Button, View, FlatList, TouchableOpacity } from 'react-native';

import mangas from './Data/Mangas.json'

const App = () => {

    const [selectedId, setSelectedId] = useState(null);


    var mangas2 = Object.keys(mangas.mangas)

    // const [isLoading, setLoading] = useState(true);
    // const [mangas, setMangas] = useState(Object.keys(mangas));

    // const getMangas = async () => {
    //     try {
    //         const response = await fetch('http://localhost:3000/Mangas');
    //         const json = await response.json();
    //         setMangas(Object.keys(json))
    //     }
    //     catch (error) {
    //         console.error(error);
    //     }
    //     finally {
    //         setLoading(false);
    //     }
    // }

    // useEffect(() => {
    //     getMangas();
    // }, []);

    const Item = ({ item, onPress, style }) => (
        <TouchableOpacity onPress={onPress} style={[styles.item, style]}>
            <Text style={{ fontSize: 20 }}>{mangas2}</Text>
        </TouchableOpacity>
    );

    const renderItem = ({ item }) => {
        const backgroundColor = item.id === selectedId ? "firebrick" : "darkslateblue";
        return (
            <Item
                item={item}
                onPress={() => setSelectedId(item.id)}
                style={{ backgroundColor }}
            />
        );
    };

    return (

        <View style = {styles.container}>
            <Text style = {styles.text}>Manga Reader</Text>
              <View style = {styles.list}>
                    <FlatList
                        data={mangas2}
                        renderItem={renderItem}
                        keyExtractor={item => item.id}
                        extractData={selectedId}
                  />
              </View>
        </View>

    );
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: 'grey',
        height: '100%',
        width: '100%',
        alignContent: 'center',
    },
    text: {
        textAlign: 'center',
        color: 'red',
        fontSize: 35,
        marginTop: 50,
    },
    list: {
      marginTop: 80,
      alignItems: 'center'
    },
    item: {
        alignSelf: 'center',
        backgroundColor: 'darkslateblue',
        padding: 15,
        marginTop: 20,
    }
});

export default App;
