import React, {useState, useEffect} from 'react';
import { View, SafeAreaView, ActivityIndicator, StyleSheet, ScrollView, Image, Dimensions } from 'react-native';
import uuid from 'react-native-uuid';

// import ImageZoom from 'react-native-image-pan-zoom';
// import ImageViewer from 'react-native-image-zoom-viewer';
// import Swiper from 'react-native-swipe-image';
// import GallerySwiper from "react-native-gallery-swiper";

const Display = ({route}) => {

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState();

    const fetchData = async (url) => {
      const response = await fetch(url);
      return response.json();
    };

    const getMangas = () => {
      try {
        // fetchData(`https://mangareader3.herokuapp.com/${route.params.title}/${route.params.chapter}`).then((data) => {
		fetchData(`https://mangareader3.herokuapp.com/One Piece/920`).then((data) => {
          setData(data);
          setLoading(false);
        });
      } catch (error) {
        console.error(error);
      }
    };


    useEffect(() => {
      getMangas();
    }, []);

    return (
      <ScrollView style={styles.container}>
        {isLoading ? <ActivityIndicator/> : (
          data.map(page=>(
				<Image key = {uuid.v4()} style={styles.page} source={{uri: page}}></Image>
          ))
        )}
    	</ScrollView>
	);
}

