import React from 'react';
import { Text, StyleSheet, Pressable, View, FlatList } from 'react-native';

// local json για testing
import data from '../Data/Experiment.json'

const Home = ({ navigation }) => {

    const pages = Object.keys(data[route_params])

    return (
        <View style = {styles.container}>
            <Text style = {styles.text}>manga_reader</Text>
                <View style = {styles.list}>
                    <FlatList
                        data={Object.keys(data)}
                        keyExtractor = {item => item}
                        renderItem={({ item }) => (
                            <Pressable onPress={() => navigation.navigate('Chapters',item)}>
                                <Text style={styles.item}>{item}</Text>
                            </Pressable>
                        )}
                    />
                </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex : 1,
        backgroundColor: 'black',
        alignContent: 'center',
    },
    text: {
        textAlign: 'center',
        color: 'white',
        fontSize: 35,
        marginTop: 50,
    },
    list: {
      marginTop: 80,
      alignItems: 'center'
    },
    item: {
        alignSelf: 'center',
        backgroundColor: 'lightblue',
        padding: 15,
        marginTop: 20,
        fontSize: 20,
    }
});

export default Home;
