import React, {useState, useEffect } from 'react';
import { Text, ActivityIndicator, StyleSheet, ScrollView, Image, Modal } from 'react-native';
import uuid from 'react-native-uuid';
// import GallerySwiper from "react-native-gallery-swiper";
// import ImageViewer from 'react-native-image-zoom-viewer';

const Display = ({route}) => {

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState();

    const fetchData = async (url) => {
      const response = await fetch(url);
      return response.json();
    };

    const getScans = () => {
      try {
        fetchData(`https://mangareader3.herokuapp.com/${route.params.title}/${route.params.chapter}`).then((data) => {
          setData(data);
		  console.log(Object.keys(data))
          setLoading(false);
        });
      } catch (error) {
        console.error(error);
      }
    };


    useEffect(() => {
      getScans();
    }, []);

    return (

      <ScrollView style={styles.container}>
        {isLoading ? <ActivityIndicator/> : (
          Object.values(data).map(page=>(
				<Image key = {uuid.v4()} style={styles.page} source={{uri : page}} ></Image>
			))
		)}
    	</ScrollView>
	);
}

