import React from 'react';
import { StyleSheet, View, Image, Text } from 'react-native';

const Pages = () => {

    const pages = "https://i.kym-cdn.com/entries/icons/original/000/035/150/Berserk_-_Guts'_Theme_0-2_screenshot.png"

    return (

        <View style={styles.container}>
            <Text style = {styles.manga_title}>Berserk</Text>

            <Image
                style={styles.page}
                source={{uri:pages}}
            />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        flex: 1,
        backgroundColor: 'black',
    },
    manga_title:{
        textAlign: 'flex-start',
        marginLeft: 30,
        color: 'White',
        fontSize: 35,
        marginTop: 50,
    },
    page:{
        marginTop: 100,
        marginBottom: 100,
        flex:1
    }
});

export default Pages;
