import React, {useState, useEffect } from 'react';
import { ScrollView, StyleSheet, Modal } from 'react-native';
// import uuid from 'react-native-uuid';
// import GallerySwiper from "react-native-gallery-swiper";
import ImageViewer from 'react-native-image-zoom-viewer';

const Display = ({route}) => {

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState();

    const fetchData = async (url) => {
      const response = await fetch(url);
      return response.json();
    };

    const getScans = () => {
      try {
        // fetchData(`https://mangareader3.herokuapp.com/${route.params.title}/${route.params.chapter}`).then((data) => {
          fetchData(`localhost:5000/${route.params.title}/${route.params.chapter}`).then((data) => {

        setData(data);
			setLoading(false);
        });
      } catch (error) {
        console.error(error);
      }
    };


    useEffect(() => {
      getScans();
    }, []);

    return (
      <ScrollView style={styles.container}>
        <Modal>
            <ImageViewer 
              imageUrls={data}
            />
        </Modal>
    </ScrollView>
	);
}

