import React, { useEffect, useState } from 'react';
import { uuid } from 'uuidv4';
import { Text, StyleSheet, Button, View, FlatList, TouchableOpacity, ActivityIndicator } from 'react-native';


const App = () => {

    const [selectedId, setSelectedId] = useState(null);

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState([]);

    const getMovies = async () => {
        try {
            const response = await fetch('https://reactnative.dev/movies.json');
            const json = await response.json();
            setData(json.movies);
        }
        catch (error) {
            console.error(error);
        }
        finally {
            setLoading(false);
        }
    }

    useEffect(() => {
        getMovies();
    }, []);

    //Content list
    const array = ["one piece", "beruseruku"]
    const mangas = [
        { id: uuid(), title: array[0], volumes: array[0] },
        { id: uuid(), title: array[1], volumes: array[1] }
    ]

    const Item = ({ item, onPress, style }) => (
        <TouchableOpacity onPress={onPress} style={[styles.item, style]}>
            <Text style={{ fontSize: 20 }}>{item.title}</Text>
        </TouchableOpacity>
    );

    const renderItem = ({ item }) => {
        const backgroundColor = item.id === selectedId ? "firebrick" : "darkslateblue";
        return (
            <Item
                item={item}
                onPress={() => setSelectedId(item.id)}
                style={{ backgroundColor : this.backgroundColor }}
            />
        );
    };

    return (

        <View style = {styles.container}>
            <Text style = {styles.text}>Manga Reader</Text>
              <View style = {styles.list}>
                {isLoading ? <ActivityIndicator/> : (  
                    <FlatList
                        data={mangas}
                        renderItem={renderItem}
                        keyExtractor={item => item.id}
                        extractData={selectedId}
                  />
                )}
              </View>
        </View>

    );
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: 'grey',
        height: '100%',
        width: '100%',
        alignContent: 'center',
    },
    text: {
        textAlign: 'center',
        color: 'red',
        fontSize: 35,
        marginTop: 50,
    },
    list: {
      marginTop: 80,
      alignItems: 'center'
    },
    item: {
        alignSelf: 'center',
        backgroundColor: 'darkslateblue',
        padding: 20,
        marginTop: 40,
    }
});

export default App;
