import React, {useState, useEffect} from 'react';
import { View, SafeAreaView, ActivityIndicator, StyleSheet, ScrollView, Image, Dimensions } from 'react-native';
import ImageZoom from 'react-native-image-pan-zoom';
import ImageViewer from 'react-native-image-zoom-viewer';

const Display = ({route}) => {

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState();

    const fetchData = async (url) => {
      const response = await fetch(url);
      return response.json();
    };

    const getMangas = () => {
      try {
        fetchData(`https://mangareader3.herokuapp.com/${route.params.title}/${route.params.chapter}`).then((data) => {
          setData(data);
          setLoading(false);
        });
      } catch (error) {
        console.error(error);
      }
    };


    useEffect(() => {
      getMangas();
    }, []);

    return (
      <ScrollView style={styles.container}>
        {isLoading ? <ActivityIndicator/> : (
          data.map(page=>(
            <ImageZoom cropWidth={Dimensions.get('window').width}
                cropHeight={Dimensions.get('window').height}
                imageWidth={200}
                imageHeight={200}
                >
				<Image style={styles.page} source={{uri: page}}></Image>
			</ImageZoom>
          ))
        )}
      </ScrollView>
	);
}

const styles = StyleSheet.create({
    container: {
        flex:1,
        backgroundColor: 'black',
    },
    page:{
        marginTop: 40,
        height:500,
        // resizeMode:'contain',
    }
});

export default Display;
