import React from 'react';
import { Text, StyleSheet, View, FlatList, TouchableOpacity, Pressable, ScrollView } from 'react-native';

// local json for testing
import data from '../Data/Mangas.json'

const Chapters = ({navigation, route}) => {

    const vols = Object.keys(data[route.params]) 

    return (
        <ScrollView style={styles.container}>
            <Text style = {styles.title}>manga_reader</Text>
            <Text style = {styles.manga_title}> {route.params}</Text>
            <FlatList 
                style={styles.itemslist}
                data = {Object.keys(data[route.params])}
                keyExtractor = {item => item}
                renderItem={({ vol }) => (
                    <TouchableOpacity>
                    <Pressable onPress={() => navigation.navigate('Display',data[route.params][vol])}>
                        <Text style = {styles.item}>{ vol }</Text>
                    </Pressable> 
                </TouchableOpacity>
                )}
            />
        </ScrollView>

    );
}

// alignText not working on android
const styles = StyleSheet.create({
    container: {
        height:'100%',
        width:'100%',
        backgroundColor: 'black',
    },
    title: {
        alignSelf: 'center',
        color: 'white',
        fontSize: 35,
        marginTop: 50,
    },
    manga_title:{
        alignSelf: 'flex-start',
        marginLeft: 30,
        color: 'red',
        fontSize: 35,
        marginTop: 50,
    },
    itemslist: {
        // flexDirection: 'row-reverse',
        // justifyContent: 'space-between',
        // flexWrap:"wrap-reverse",
    },
    item: {
      marginTop: 20,
      fontSize: 20,
      backgroundColor: 'lightblue',
      padding: 10,
    }
});

export default Chapters;
